
public class PercentageTotalMarks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s1 = 78;
		int s2 = 45;
		int s3 = 62;
		
		int tm = s1 + s2 + s3;
		
		System.out.println("Total Marks: " + tm);
		
		double per = (tm / 300.0) * 100;
		
		System.out.println("Total Percentage: " + per);
		
		
		

	}

}
